import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Viewer() {
  const { id } = useParams(); // Obtiene el ID del capítulo desde la URL
  const [pages, setPages] = useState([]);
  const [currentPageIndex, setCurrentPageIndex] = useState(0); // Índice de la página actual
  const [loading, setLoading] = useState(true);

  // Fetch de las páginas del capítulo desde la API
  useEffect(() => {
    const fetchPages = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/chapters/${id}/pages`);
        setPages(response.data);
      } catch (error) {
        console.error('Error fetching pages:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPages();
  }, [id]); // Se ejecuta cada vez que cambia la ID del capítulo

  const handleNext = () => {
    if (currentPageIndex < pages.length - 1) {
      setCurrentPageIndex(currentPageIndex + 1);
    }
  };

  const handlePrevious = () => {
    if (currentPageIndex > 0) {
      setCurrentPageIndex(currentPageIndex - 1);
    }
  };

  if (loading) {
    return <p>Cargando páginas...</p>;
  }

  if (pages.length === 0) {
    return <p>No hay páginas para este capítulo.</p>;
  }

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>PanelView</h1>
      <div>
        <img
          src={`/comics/${pages[currentPageIndex].image_url}`} // URL de la imagen actual
          alt={`Page ${pages[currentPageIndex].page_number}`}
          style={{ width: '80%', maxHeight: '80vh', objectFit: 'contain' }}
        />
      </div>
      <div style={{ marginTop: '20px' }}>
        <button onClick={handlePrevious} disabled={currentPageIndex === 0}>
          Anterior
        </button>
        <span style={{ margin: '0 10px' }}>
          Página {currentPageIndex + 1} de {pages.length}
        </span>
        <button onClick={handleNext} disabled={currentPageIndex === pages.length - 1}>
          Siguiente
        </button>
      </div>
    </div>
  );
}

export default Viewer;
