import React, { useEffect, useState } from 'react';
import axios from 'axios';
import LightGallery from 'lightgallery/react';
import 'lightgallery/css/lightgallery.css';
import { useParams } from 'react-router-dom';

function Viewer() {
  const { id } = useParams(); // Obtiene el ID del capítulo desde la URL
  const [pages, setPages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentImageIndex, setCurrentImageIndex] = useState(0); // Estado para manejar la imagen seleccionada

  // Fetch de las páginas del capítulo desde la API
  useEffect(() => {
    const fetchPages = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/chapters/${id}/pages`);
        setPages(response.data);
      } catch (error) {
        console.error('Error fetching pages:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPages();
  }, [id]); // Se ejecuta cada vez que cambia la ID del capítulo

  // Maneja el cambio de imagen en la galería
  const handleSlide = (index) => {
    setCurrentImageIndex(index); // Actualiza el índice de la imagen seleccionada
  };

  // Si la carga está en proceso, mostramos el mensaje de carga
  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>PanelView</h1>

      {/* Mostramos solo la imagen actual según el índice seleccionado */}
      <div style={{ textAlign: 'center' }}>
        {pages.length > 0 ? (
          <LightGallery
            speed={500}
            mode="lg-fade"
            onSlide={handleSlide} // Actualiza el índice cuando cambia la imagen en la galería
            index={currentImageIndex} // Establece el índice actual en la galería
          >
            {/* Imagen visible en la página, controlada por el índice de la galería */}
            <a href={`/comics/${pages[currentImageIndex].image_url}`} key={pages[currentImageIndex].id}>
              <img
                src={`/comics/${pages[currentImageIndex].image_url}`}
                alt={`Page ${pages[currentImageIndex].page_number}`}
                style={{ width: '80%', cursor: 'pointer' }}
              />
            </a>

            {/* Todas las imágenes para la galería */}
            {pages.map((page, index) => (
              <a href={`/comics/${page.image_url}`} key={page.id}>
                <img
                  src={`/comics/${page.image_url}`}
                  alt={`Page ${page.page_number}`}
                  style={{ display: 'none' }} // Las imágenes son ocultadas fuera de la galería
                />
              </a>
            ))}
          </LightGallery>
        ) : (
          <p>No pages available for this chapter.</p>
        )}
      </div>
    </div>
  );
}

export default Viewer;
