import React, { useEffect, useState } from 'react';
import axios from 'axios';
import LightGallery from 'lightgallery/react';

// Importar estilos y plugins de LightGallery
import 'lightgallery/css/lightgallery.css';
import 'lightgallery/css/lg-thumbnail.css';
import 'lightgallery/css/lg-zoom.css';

import lgThumbnail from 'lightgallery/plugins/thumbnail';
import lgZoom from 'lightgallery/plugins/zoom';

function Viewer() {
  const { id } = useParams(); // Obtiene el ID del capítulo desde la URL
  const [pages, setPages] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch de las páginas del capítulo desde la API
  useEffect(() => {
    const fetchPages = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/chapters/${id}/pages`);
        setPages(response.data);
      } catch (error) {
        console.error('Error fetching pages:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPages();
  }, [id]);

  if (loading) {
    return <p>Cargando páginas...</p>;
  }

  if (pages.length === 0) {
    return <p>No hay páginas para este capítulo.</p>;
  }

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Galería de Imágenes</h1>

      {/* LightGallery */}
      <LightGallery
        speed={500} // Velocidad de las transiciones
        plugins={[lgThumbnail, lgZoom]} // Plugins de LightGallery
      >
        {pages.map((page) => (
          <a
            key={page.page_number}
            href={`/comics/${page.image_url}`} // Imagen completa
            data-sub-html={`<h4>Página ${page.page_number}</h4>`} // Texto descriptivo opcional
          >
            <img
              src={`/comics/${page.image_url}`} // Miniatura de la imagen
              alt={`Page ${page.page_number}`}
              style={{
                width: '200px',
                height: 'auto',
                cursor: 'pointer',
                margin: '10px',
              }}
            />
          </a>
        ))}
      </LightGallery>
    </div>
  );
}

export default Viewer;
