import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Para obtener el ID de la URL

function Viewer() {
  const { id } = useParams(); // Obtiene el ID del capítulo desde la URL
  const [pages, setPages] = useState([]); // Lista de páginas del capítulo
  const [currentPageIndex, setCurrentPageIndex] = useState(0); // Índice de la página actual
  const [loading, setLoading] = useState(true);

  // Fetch de las páginas del capítulo desde la API
  useEffect(() => {
    const fetchPages = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/chapters/${id}/pages`);
        setPages(response.data);
      } catch (error) {
        console.error('Error fetching pages:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPages();
  }, [id]); // Se ejecuta cada vez que cambia la ID del capítulo

  // Cambiar a la página siguiente
  const nextPage = () => {
    setCurrentPageIndex((prevIndex) => (prevIndex + 1) % pages.length); // Cicla al inicio al llegar al final
  };

  // Cambiar a la página anterior
  const prevPage = () => {
    setCurrentPageIndex((prevIndex) => (prevIndex - 1 + pages.length) % pages.length); // Cicla al final si está en la primera
  };

  if (loading) {
    return <p>Cargando páginas...</p>;
  }

  if (pages.length === 0) {
    return <p>No hay páginas disponibles para este capítulo.</p>;
  }

  // Obtener la imagen actual
  const currentPage = pages[currentPageIndex];

  return (
    <div>
      <h1>Capítulo {id}</h1>
      <div style={{ textAlign: 'center' }}>
        <div>
          {/* Imagen actual */}
          <img
            src={`/comics/${currentPage.image_url}`} // URL de la imagen actual
            alt={`Page ${currentPage.page_number}`}
            style={{ maxWidth: '100%', maxHeight: '80vh', objectFit: 'contain' }}
          />
        </div>
        {/* Controles de navegación */}
        <div style={{ marginTop: '20px' }}>
          <button onClick={prevPage} disabled={pages.length <= 1}>
            Anterior
          </button>
          <span style={{ margin: '0 15px' }}>
            Página {currentPageIndex + 1} de {pages.length}
          </span>
          <button onClick={nextPage} disabled={pages.length <= 1}>
            Siguiente
          </button>
        </div>
      </div>
    </div>
  );
}

export default Viewer;
