import React, { useEffect, useState } from 'react';
import axios from 'axios';
import LightGallery from 'lightgallery/react';
import 'lightgallery/css/lightgallery.css';
import { useParams } from 'react-router-dom'; // Para obtener el ID de la URL

function Viewer() {
  const { id } = useParams(); // Obtiene el ID del capítulo desde la URL
  const [pages, setPages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentImageIndex, setCurrentImageIndex] = useState(0); // Índice de la imagen actualmente visible

  // Fetch de las páginas del capítulo desde la API
  useEffect(() => {
    const fetchPages = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/chapters/${id}/pages`);
        setPages(response.data);
      } catch (error) {
        console.error('Error fetching pages:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPages();
  }, [id]); // Se ejecuta cada vez que cambia la ID del capítulo

  if (loading) {
    return <p>Loading...</p>;
  }

  // Función para cambiar a la imagen anterior
  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? pages.length - 1 : prevIndex - 1
    );
  };

  // Función para cambiar a la imagen siguiente
  const handleNext = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === pages.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>PanelView</h1>

      {/* Imagen destacada (solo una a la vez) */}
      {pages.length > 0 && (
        <div>
          <img
            src={`/comics/${pages[currentImageIndex].image_url}`}
            alt={`Page ${pages[currentImageIndex].page_number}`}
            style={{ width: '80%', maxHeight: '500px', marginBottom: '20px' }}
          />
          <div>
            <button onClick={handlePrev} style={{ marginRight: '10px' }}>
              Anterior
            </button>
            <button onClick={handleNext}>Siguiente</button>
          </div>
        </div>
      )}

      {/* Galería LightGallery */}
      <LightGallery speed={500}>
        {pages.map((page) => (
          <a href={`/comics/${page.image_url}`} key={page.id}>
            <img
              src={`/comics/${page.image_url}`}
              alt={`Page ${page.page_number}`}
              style={{
                display: 'none', // Ocultamos las imágenes dentro del contenedor de LightGallery
              }}
            />
          </a>
        ))}
      </LightGallery>
    </div>
  );
}

export default Viewer;
