import { useSearchParams } from "react-router-dom";
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ComicDetail() {
    const [searchParams, setSearchParams] = useSearchParams();
    const [comic, setComic] = useState(null); // Almacena los datos del cómic
    const [loading, setLoading] = useState(true); // Estado de carga

    // Obtén el ID del cómic desde los parámetros de la URL
    const comicId = searchParams.get('id');

    // useEffect para cargar los datos del cómic cuando cambia el ID
    useEffect(() => {
        const fetchComic = async () => {
            if (comicId) {
                setLoading(true); // Comienza el estado de carga
                try {
                    const response = await axios.get(`http://localhost:5000/api/mangas?id=${comicId}`);
                    setComic(response.data); // Actualiza los datos del cómic
                } catch (error) {
                    console.error('Error al obtener los datos del cómic:', error);
                } finally {
                    setLoading(false); // Finaliza la carga
                }
            }
        };

        fetchComic();
    }, [comicId]); // Se ejecuta cada vez que cambia el ID del cómic

    return (
        <div>
            <h1>Detalle del Cómic</h1>

            {loading ? (
                <p>Cargando cómic...</p>
            ) : comic ? (
                <div>
                    <h2>{mangas.title}</h2>
                    <p><strong>Autor:</strong> {mangas.author}</p>
                    <p><strong>Género:</strong> {mangas.genre}</p>
                    <p><strong>Sinopsis:</strong> {mangas.synopsis}</p>
                </div>
            ) : (
                <p>No se encontró el cómic.</p>
            )}

            <button onClick={() => setSearchParams({ id: '2' })}>Ir al Cómic 2</button>
        </div>
    );
}

export default ComicDetail;
