import React, { useEffect, useState } from 'react';
import axios from 'axios';
import LightGallery from 'lightgallery/react';
import 'lightgallery/css/lightgallery.css';
import { useParams } from 'react-router-dom'; // Para obtener el ID de la URL

function Viewer() {
  const { id } = useParams(); // Obtiene el ID del capítulo desde la URL
  const [pages, setPages] = useState([]);
  const [loading, setLoading] = useState(true);

  const lightGalleryRef = useRef < LightGallery > null;
  const containerRef = useRef(null);
  const [galleryContainer, setGalleryContainer] = useState(null);

  const onInit = useCallback((detail) => {
    if (detail) {
        lightGalleryRef.current = detail.instance;
        lightGalleryRef.current.openGallery();
    }
}, []);

useEffect(() => {
    if (containerRef.current) {
        setGalleryContainer(containerRef.current);
    }
}, []);

  // Fetch de las páginas del capítulo desde la API
  useEffect(() => {
    const fetchPages = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/chapters/${id}/pages`);
        setPages(response.data);
      } catch (error) {
        console.error('Error fetching pages:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPages();
  }, [id]); // Se ejecuta cada vez que cambia la ID del capítulo

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>PanelView</h1>
      <LightGallery speed={500} container={galleryContainer} onInit={onInit} dynamic={true}>
        {pages.length > 0 ? (
          pages.map((page) => (
            <a href={`/comics/${page.image_url}`} key={page.id}>
              <img
                src={`/comics/${page.image_url}`}
                alt={`Page ${page.page_number}`}
                style={{ width: '100%', cursor: 'pointer' }}
              />
            </a>
          ))
        ) : (
          <p>No pages available for this chapter.</p>
        )}
      </LightGallery>
    </div>
  );
}

export default Viewer;
