import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { LightgalleryProvider, LightgalleryItem } from 'react-lightgallery';
import 'lightgallery.js/dist/css/lightgallery.css';
import { useParams } from 'react-router-dom'; // Para obtener el ID de la URL

function Viewer() {
  const { id } = useParams(); // Obtiene el ID del capítulo desde la URL
  const [pages, setPages] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch de las páginas del capítulo desde la API
  useEffect(() => {
    const fetchPages = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/chapters/${id}/pages`);
        setPages(response.data);
      } catch (error) {
        console.error('Error fetching pages:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPages();
  }, [id]); // Se ejecuta cada vez que cambia la ID del capítulo

  if (loading) {
    return <p>Cargando páginas...</p>;
  }

  return (
    <div>
      <h1>Páginas del Capítulo</h1>
      {pages.length > 0 ? (
        <LightgalleryProvider>
          {pages.map((page) => (
            <LightgalleryItem key={page.id} group="group1" src={page.image_url}>
              <img
                src={`/comics/${page.image_url}`} // La URL de la imagen de la página
                alt={`Page ${page.page_number}`}
                style={{ width: '100%', cursor: 'pointer' }}
              />
            </LightgalleryItem>
          ))}
        </LightgalleryProvider>
      ) : (
        <p>No pages for this chapter yet!</p>
      )}
    </div>
  );
}

export default Viewer;
